import React, { useContext } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link, useNavigate, useLocation } from 'react-router-dom';
import { AuthProvider, AuthContext } from './AuthContext';
import './App.css';
import logo from './logo.png';
import Flashcard from './flashcard.js';
import OXQuiz from './OXquiz.js';
import FillIn from './fillin.js';
import LoginForm from './LoginForm';
import SignUpForm from './SignUpForm';

function App() {
  return (
    <AuthProvider>
      <Router>
        <div className="container">
          <Header />
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<LoginFormWrapper />} />
            <Route path="/signup" element={<SignUpFormWrapper />} />
            <Route path="/flashcard" element={<PrivateRoute component={Flashcard} />} />
            <Route path="/oxquiz" element={<PrivateRoute component={OXQuiz} />} />
            <Route path="/fillin" element={<PrivateRoute component={FillIn} />} />
          </Routes>
        </div>
      </Router>
    </AuthProvider>
  );
}

// Header 컴포넌트
function Header() {
  const { user, logout } = useContext(AuthContext);

  return (
    <header>
      <div className="header-left">
        <img src={logo} alt="Logo" className="logo" />
        <button className="add-button">
          <Link to="/">홈</Link>
        </button>
      </div>
      <div className="user-info">
        {user ? (
          <>
            <span>{user.email}님 환영합니다!</span>
            <button onClick={logout} className="logout-button">로그아웃</button>
          </>
        ) : (
          <>
            <button className="login-button">
              <Link to="/login">로그인</Link>
            </button>
            <button className="signup-button">
              <Link to="/signup">회원가입</Link>
            </button>
          </>
        )}
      </div>
    </header>
  );
}

// Home 컴포넌트
function Home() {
  const { user } = useContext(AuthContext);

  return (
    <main>
      <h1>단어장 앱</h1>
      <p>{user ? "학습을 시작하세요!" : "로그인 후 이용 가능합니다."}</p>
    </main>
  );
}

// 회원가입 페이지
function SignUpFormWrapper() {
  const { signUp } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleSignUp = async (email, password) => {
    try {
      await signUp(email, password);
      alert('회원가입 성공! 자동으로 로그인되었습니다.');
      navigate('/'); // 홈 화면으로 이동
    } catch (err) {
      alert('회원가입 실패: ' + err.message);
    }
  };

  return <SignUpForm onSignUp={handleSignUp} />;
}

// 로그인 페이지
function LoginFormWrapper() {
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();
  const location = useLocation();
  const from = location.state?.from?.pathname || '/';

  const handleLogin = async (email, password) => {
    try {
      await login(email, password);
      alert('로그인 성공!');
      navigate(from);
    } catch (err) {
      alert('로그인 실패: ' + err.message);
    }
  };

  return <LoginForm onLogin={handleLogin} />;
}

// PrivateRoute
function PrivateRoute({ component: Component }) {
  const { user } = useContext(AuthContext);
  const location = useLocation();

  if (!user) {
    return <Navigate to="/login" state={{ from: location }} />;
  }

  return <Component />;
}

export default App;
