// App.js
import React, { useContext } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { AuthProvider, AuthContext } from './AuthContext'; // 로그인 상태 관리
import Modal from './Modal';
import './App.css';
import logo from './logo.png';
import Flashcard from './flashcard.js';
import OXQuiz from './OXquiz.js';
import FillIn from './fillin.js';
import LoginForm from './LoginForm'; // 로그인 페이지

function App() {
  return (
    <AuthProvider>
      <Router>
        <div className="container">
          <Header />
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<LoginFormWrapper />} />
            <Route path="/flashcard" element={<PrivateRoute component={Flashcard} />} />
            <Route path="/oxquiz" element={<PrivateRoute component={OXQuiz} />} />
            <Route path="/fillin" element={<PrivateRoute component={FillIn} />} />
          </Routes>
        </div>
      </Router>
    </AuthProvider>
  );
}

// Header 컴포넌트: 로그인 상태에 따라 다르게 표시
function Header() {
  const { user, logout } = useContext(AuthContext);

  return (
    <header>
      <div className="header-left">
        <img src={logo} alt="Logo" className="logo" />
        <button className="add-button">
          <Link to="/">홈</Link>
        </button>
      </div>
      <div className="user-info">
        {user ? (
          <>
            <span>{user.email}님 환영합니다!</span>
            <button onClick={logout} className="logout-button">로그아웃</button>
          </>
        ) : (
          <button className="login-button">
            <Link to="/login">로그인</Link>
          </button>
        )}
      </div>
    </header>
  );
}

// Home 컴포넌트
function Home() {
  const { user } = useContext(AuthContext);

  return (
    <main>
      <h1>단어장 앱</h1>
      <p>{user ? "학습을 시작하세요!" : "로그인 후 이용 가능합니다."}</p>
    </main>
  );
}

// 로그인 페이지 래퍼
function LoginFormWrapper() {
  const { login } = useContext(AuthContext);

  const handleLogin = async (email, password) => {
    try {
      await login(email, password); // 로그인 로직 호출
      alert('로그인 성공!');
    } catch (err) {
      alert('로그인 실패: ' + err.message);
    }
  };

  return <LoginForm onLogin={handleLogin} />;
}

// PrivateRoute: 인증된 사용자만 접근 가능
function PrivateRoute({ component: Component }) {
  const { user } = useContext(AuthContext);

  if (!user) {
    return (
      <div>
        <p>접근 불가: 로그인이 필요합니다.</p>
        <Link to="/login">로그인 페이지로 이동</Link>
      </div>
    );
  }

  return <Component />;
}

export default App;
